VALUES ('Tiny Talks', '{"en": "If you could give every animal a job, what job would you give a giraffe?", "da": "Hvis du kunne give alle dyr et job, hvilket job ville du give en giraf?", "de": "Wenn du jedem Tier einen Job geben könntest, welchen würdest du einer Giraffe geben?", "es": "Si pudieras darle un trabajo a cada animal, ¿qué trabajo le darías a una jirafa?", "pt": "Se pudesses dar um trabalho a cada animal, que trabalho darias a uma girafa?", "ro": "Dacă ai putea da un loc de muncă fiecărui animal, ce slujbă i-ai da unei girafe?", "fr": "Si tu pouvais donner un travail à chaque animal, quel travail donnerais-tu à une girafe ?", "it": "Se potessi dare a ogni animale un lavoro, che lavoro daresti a una giraffa?", "fi": "Jos voisit antaa jokaiselle eläimelle työn, minkä työn antaisit kirahville?", "nl": "Als je elk dier een baan zou kunnen geven, welke baan zou je dan aan een giraffe geven?", "no": "Hvis du kunne gi hvert dyr en jobb, hvilken jobb ville du gitt en giraff?", "sv": "Om du kunde ge varje djur ett jobb, vilket jobb skulle du ge en giraff?", "pl": "Gdybyś mógł nadać każdemu zwierzęciu pracę, jaką pracę dałbyś żyrafie?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
