INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Tiny Talks', '{"en": "What’s something you think your parents work hard to do every day?", "da": "Hvad er noget, du tror, dine forældre arbejder hårdt på hver dag?", "de": "Woran arbeiten deine Eltern deiner Meinung nach jeden Tag hart?", "es": "¿En qué crees que trabajan duro tus padres cada día?", "pt": "Em que achas que os teus pais trabalham arduamente todos os dias?", "ro": "La ce crezi că muncesc din greu părinții tăi în fiecare zi?", "fr": "À quoi penses-tu que tes parents travaillent dur chaque jour ?", "it": "Qual è una cosa che pensi i tuoi genitori si impegnino a fare ogni giorno?", "fi": "Mikä on jotain, minkä uskot vanhempiesi tekevän kovasti joka päivä?", "nl": "Wat is iets waarvan je denkt dat je ouders elke dag hard voor werken?", "no": "Hva er noe du tror foreldrene dine jobber hardt for å gjøre hver dag?", "sv": "Vad är något du tror att dina föräldrar arbetar hårt för att göra varje dag?", "pl": "Co myślisz, że twoi rodzice codziennie ciężko pracują, aby to zrobić?"}'::jsonb, false, true, true);
