INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('Tiny Talks', '{"en": "What do you think the Tooth Fairy does with all those teeth?", "da": "Hvad tror du, Tandfeen gør med alle de tænder?", "de": "Was glaubst du, was die Zahnfee mit all den Zähnen macht?", "es": "¿Qué crees que hace el Hada de los Dientes con todos esos dientes?", "pt": "O que achas que a Fada dos Dentes faz com todos esses dentes?", "ro": "Ce crezi că face Zâna Măseluță cu toți acei dinți?", "fr": "Que penses-tu que fait la Petite Souris avec toutes ces dents ?", "it": "Cosa pensi che faccia la Fatina dei Denti con tutti quei denti?", "fi": "Mitä mieltä olet, mitä Hammaskeiju tekee kaikilla niillä hampailla?", "nl": "Wat denk je dat de Tandfee met al die tanden doet?", "no": "Hva tror du Tandfeen gjør med alle de tennene?", "sv": "Vad tror du Tandfen gör med alla de där tänderna?", "pl": "Co myślisz, co Wróżka Zębuszka robi ze wszystkimi tymi zębami?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
