VALUES ('The Good Old Days Talks', '{"en": "What’s something that’s gotten too complicated today?", "da": "Hvad er noget, der er blevet alt for kompliceret i dag?", "de": "Was ist heute viel zu kompliziert geworden?", "es": "¿Qué cosa se ha vuelto demasiado complicada hoy en día?", "pt": "O que ficou demasiado complicado hoje em dia?", "ro": "Ce lucru a devenit prea complicat azi?", "fr": "Qu’est-ce qui est devenu trop compliqué de nos jours ?", "it": "Qual è qualcosa che è diventato troppo complicato oggi?", "fi": "Mikä on jotain, mikä on tänään mennyt liian monimutkaiseksi?", "nl": "Wat is iets dat vandaag te ingewikkeld is geworden?", "no": "Hva er noe som har blitt for komplisert i dag?", "sv": "Vad är något som har blivit för komplicerat idag?", "pl": "Coś, co stało się dzisiaj zbyt skomplikowane?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
