INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "Tell me about how you met the love of your life.", "da": "Fortæl mig, hvordan du mødte kærligheden i dit liv.", "de": "Erzähl mir, wie du die Liebe deines Lebens getroffen hast.", "es": "Cuéntame cómo conociste al amor de tu vida.", "pt": "Conta-me como conheceste o amor da tua vida.", "ro": "Spune-mi cum ți-ai întâlnit dragostea vieții tale.", "fr": "Raconte-moi comment tu as rencontré l’amour de ta vie.", "it": "Raccontami di come hai incontrato l''amore della tua vita.", "fi": "Kerro minulle, miten tapasit elämäsi rakkauden.", "nl": "Vertel me hoe je de liefde van je leven hebt ontmoet.", "no": "Fortell meg om hvordan du møtte kjærligheten i livet ditt.", "sv": "Berätta om hur du träffade kärleken i ditt liv.", "pl": "Opowiedz mi, jak poznałeś miłość swojego życia."}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s one thing you think people appreciate less now than before?", "da": "Hvad er én ting, du tror, folk værdsætter mindre nu end før?", "de": "Was ist eine Sache, die die Menschen heute weniger schätzen als früher?", "es": "¿Qué cosa crees que la gente aprecia menos ahora que antes?", "pt": "O que achas que as pessoas valorizam menos agora do que antes?", "ro": "Ce lucru crezi că oamenii apreciază mai puțin acum decât înainte?", "fr": "Quelle chose penses-tu que les gens apprécient moins aujourd’hui qu’avant ?", "it": "Qual è una cosa che pensi le persone apprezzino meno adesso rispetto a prima?", "fi": "Mikä on yksi asia, jota luulet ihmisten arvostavan vähemmän nyt kuin ennen?", "nl": "Wat is één ding dat je denkt dat mensen nu minder waarderen dan vroeger?", "no": "Hva er én ting du tror folk setter mindre pris på nå enn før?", "sv": "Vad är en sak som du tror att folk uppskattar mindre nu än tidigare?", "pl": "Coś, co myślisz, że ludzie mniej doceniają teraz niż wcześniej?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something people used to make at home instead of buying?", "da": "Hvad er noget, folk plejede at lave derhjemme i stedet for at købe?", "de": "Was haben die Leute früher zu Hause gemacht, anstatt es zu kaufen?", "es": "¿Qué solían hacer las personas en casa en lugar de comprarlo?", "pt": "O que as pessoas costumavam fazer em casa em vez de comprar?", "ro": "Ce obișnuiau oamenii să facă acasă în loc să cumpere?", "fr": "Que faisaient les gens autrefois chez eux au lieu de l’acheter ?", "it": "Qualcosa che le persone facevano in casa invece di comprare?", "fi": "Mikä on jotain, mitä ihmiset ennen tekivät kotona sen sijaan, että ostaisivat sen?", "nl": "Wat is iets dat mensen vroeger thuis maakten in plaats van het te kopen?", "no": "Hva er noe folk pleide å lage hjemme i stedet for å kjøpe?", "sv": "Vad är något som folk brukade göra hemma istället för att köpa?", "pl": "Coś, co ludzie kiedyś robili w domu zamiast kupować?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What was your first memory of seeing a computer, phone, or car?", "da": "Hvad er din første erindring om at se en computer, telefon eller bil?", "de": "Was ist deine erste Erinnerung daran, einen Computer, ein Telefon oder ein Auto gesehen zu haben?", "es": "¿Cuál es tu primer recuerdo de ver una computadora, un teléfono o un coche?", "pt": "Qual é a tua primeira lembrança de ver um computador, um telefone ou um carro?", "ro": "Care este prima ta amintire de când ai văzut un computer, un telefon sau o mașină?", "fr": "Quel est ton premier souvenir d’avoir vu un ordinateur, un téléphone ou une voiture ?", "it": "Qual è stato il tuo primo ricordo di aver visto un computer, un telefono o un''auto?", "fi": "Mikä oli ensimmäinen muistosi tietokoneen, puhelimen tai auton näkemisestä?", "nl": "Wat is je eerste herinnering aan het zien van een computer, telefoon of auto?", "no": "Hva var ditt første minne om å se en datamaskin, telefon eller bil?", "sv": "Vad är ditt första minne av att se en dator, telefon eller bil?", "pl": "Jakie jest twoje pierwsze wspomnienie związane z widzeniem komputera, telefonu lub samochodu?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something that cost very little back then but is expensive now?", "da": "Hvad er noget, der kostede meget lidt dengang, men som er dyrt nu?", "de": "Was hat früher sehr wenig gekostet, ist aber heute teuer?", "es": "¿Qué cosa costaba muy poco antes pero ahora es cara?", "pt": "O que custava muito pouco antes, mas é caro agora?", "ro": "Ce lucru costa foarte puțin atunci, dar e scump acum?", "fr": "Quelle chose coûtait très peu autrefois mais est chère aujourd’hui ?", "it": "Qualcosa che costava molto poco allora ma adesso è costoso?", "fi": "Mikä on jotain, mikä maksoi ennen hyvin vähän, mutta on nyt kallista?", "nl": "Wat is iets dat vroeger heel weinig kostte maar nu duur is?", "no": "Hva er noe som kostet veldig lite den gangen, men som er dyrt nå?", "sv": "Vad är något som kostade väldigt lite då men som är dyrt nu?", "pl": "Coś, co kiedyś kosztowało bardzo mało, a teraz jest drogie?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something people used to do more slowly — and that was a good thing?", "da": "Hvad er noget, folk plejede at gøre langsommere — og det var godt?", "de": "Was haben die Leute früher langsamer gemacht – und das war gut so?", "es": "¿Qué hacían las personas más despacio antes — y era algo bueno?", "pt": "O que as pessoas faziam mais devagar antigamente — e era bom?", "ro": "Ce făceau oamenii mai încet înainte — și era un lucru bun?", "fr": "Quelle chose les gens faisaient-ils plus lentement avant — et c’était bien ainsi ?", "it": "Qualcosa che le persone facevano più lentamente — e che era una cosa positiva?", "fi": "Mikä on jotain, mitä ihmiset ennen tekivät hitaammin — ja se oli hyvä asia?", "nl": "Wat is iets dat mensen vroeger langzamer deden — en dat een goede zaak was?", "no": "Hva er noe folk pleide å gjøre saktere — og som var en god ting?", "sv": "Vad är något som folk brukade göra långsammare — och som var en bra sak?", "pl": "Coś, co ludzie robili kiedyś wolniej — i to było dobre?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What kind of music did you listen to, and how did you play it?", "da": "Hvilken slags musik lyttede du til, og hvordan spillede du den?", "de": "Welche Musik hast du gehört, und wie hast du sie gespielt?", "es": "¿Qué tipo de música escuchabas y cómo la reproducías?", "pt": "Que tipo de música ouvias e como a tocavas?", "ro": "Ce fel de muzică ascultai și cum o redai?", "fr": "Quel genre de musique écoutais-tu et comment la jouais-tu ?", "it": "Che tipo di musica ascoltavi e come la suonavi?", "fi": "Millaista musiikkia kuuntelit ja miten soitit sitä?", "nl": "Welke soort muziek luisterde je naar, en hoe speelde je het?", "no": "Hvilken type musikk hørte du på, og hvordan spilte du den?", "sv": "Vilken typ av musik lyssnade du på, och hur spelade du den?", "pl": "Jakiej muzyki słuchałeś i jak ją odtwarzałeś?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "Tell me about what people used to do for fun when you were young.", "da": "Fortæl mig, hvad folk plejede at gøre for sjov, da du var ung.", "de": "Erzähl mir, was die Leute früher zum Spaß gemacht haben, als du jung warst.", "es": "Cuéntame qué hacía la gente para divertirse cuando eras joven.", "pt": "Conta-me o que as pessoas faziam para se divertir quando eras jovem.", "ro": "Spune-mi ce făceau oamenii pentru distracție când erai tânăr.", "fr": "Raconte-moi ce que les gens faisaient pour s’amuser quand tu étais jeune.", "it": "Raccontami cosa facevano le persone per divertirsi quando eri giovane.", "fi": "Kerro minulle, mitä ihmiset tekivät hauskaa nuoruudessasi.", "nl": "Vertel me wat mensen vroeger deden voor plezier toen je jong was.", "no": "Fortell meg om hva folk pleide å gjøre for moro skyld da du var ung.", "sv": "Berätta om vad folk brukade göra för skoj när du var ung.", "pl": "Opowiedz mi, co ludzie robili dla zabawy, gdy byłeś młody."}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What did “style” look like when you were growing up?", "da": "Hvordan så “stil” ud, da du voksede op?", "de": "Wie sah „Stil“ aus, als du aufgewachsen bist?", "es": "¿Cómo era el “estilo” cuando crecías?", "pt": "Como era o “estilo” quando cresceste?", "ro": "Cum arăta „stilul” când erai copil?", "fr": "À quoi ressemblait le “style” quand tu grandissais ?", "it": "Com''era lo \"stile\" quando sei cresciuto?", "fi": "Millainen \"tyyli\" oli, kun kasvoit?", "nl": "Hoe zag \"stijl\" eruit toen je opgroeide?", "no": "Hvordan så \"stil\" ut da du vokste opp?", "sv": "Hur såg \"stil\" ut när du växte upp?", "pl": "Jak wyglądał „styl”, gdy dorastałeś?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something from the past you’re glad didn’t stick around?", "da": "Hvad er noget fra fortiden, du er glad for ikke hænger ved?", "de": "Was aus der Vergangenheit bist du froh, dass es nicht geblieben ist?", "es": "¿Qué cosa del pasado te alegra que ya no exista?", "pt": "O que do passado te alegra que já não exista?", "ro": "Ce lucru din trecut te bucuri că nu a rămas?", "fr": "Quelle chose du passé es-tu content(e) qu’elle n’existe plus ?", "it": "Qualcosa del passato di cui sei contento che non sia rimasto?", "fi": "Mikä on jokin menneisyydestä, josta olet iloinen, ettei se jäänyt pysyväksi?", "nl": "Wat is iets uit het verleden waar je blij mee bent dat het niet is gebleven?", "no": "Hva er noe fra fortiden du er glad for ikke ble værende?", "sv": "Vad är något från det förflutna som du är glad att det inte stannade kvar?", "pl": "Coś z przeszłości, z czego cieszysz się, że nie przetrwało?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s one thing you think technology ruined?", "da": "Hvad er én ting, du tror, teknologien ødelagde?", "de": "Was ist eine Sache, von der du denkst, dass die Technologie sie ruiniert hat?", "es": "¿Qué es algo que crees que la tecnología arruinó?", "pt": "O que achas que a tecnologia arruinou?", "ro": "Ce crezi că a stricat tehnologia?", "fr": "Quelle chose penses-tu que la technologie a gâchée ?", "it": "Qual è una cosa che pensi la tecnologia abbia rovinato?", "fi": "Mikä on yksi asia, jonka luulet teknologian pilanneen?", "nl": "Wat is één ding waarvan je denkt dat technologie het verpest heeft?", "no": "Hva er én ting du mener teknologi har ødelagt?", "sv": "Vad är en sak som du tycker att teknologin har förstört?", "pl": "Co według ciebie technologia zrujnowała?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something that made life simpler back then?", "da": "Hvad er noget, der gjorde livet enklere dengang?", "de": "Was hat das Leben früher einfacher gemacht?", "es": "¿Qué hacía la vida más simple antes?", "pt": "O que tornava a vida mais simples antigamente?", "ro": "Ce făcea viața mai simplă atunci?", "fr": "Qu’est-ce qui rendait la vie plus simple autrefois ?", "it": "Qualcosa che ha reso la vita più semplice allora?", "fi": "Mikä oli jotain, joka teki elämän yksinkertaisemmaksi silloin?", "nl": "Wat is iets dat het leven vroeger eenvoudiger maakte?", "no": "Hva er noe som gjorde livet enklere den gangen?", "sv": "Vad är något som gjorde livet enklare då?", "pl": "Coś, co upraszczało życie w tamtych czasach?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something about today you wouldn’t trade for anything?", "da": "Hvad er noget ved i dag, du ikke ville bytte for noget?", "de": "Was ist etwas an heute, das du für nichts eintauschen würdest?", "es": "¿Qué cosa de hoy no cambiarías por nada?", "pt": "O que de hoje não trocarias por nada?", "ro": "Ce este ceva din prezent pe care nu l-ai schimba pentru nimic?", "fr": "Quelle chose d’aujourd’hui ne changerais-tu pour rien au monde ?", "it": "Qualcosa di oggi che non scambieresti per nulla?", "fi": "Mikä on jotain tänään, jota et vaihtaisi mihinkään?", "nl": "Wat is iets van vandaag dat je voor niets zou willen ruilen?", "no": "Hva er noe med i dag du ikke ville byttet bort for noe?", "sv": "Vad är det som hände idag som du inte skulle byta mot något?", "pl": "Coś, co dzisiaj masz, za nic byś nie zamienił?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something that people were stricter about in your time?", "da": "Hvad var folk strengere med i din tid?", "de": "Womit waren die Leute in deiner Zeit strenger?", "es": "¿Con qué eran más estrictas las personas en tu época?", "pt": "Com o que as pessoas eram mais rigorosas no teu tempo?", "ro": "Cu ce erau oamenii mai stricți în vremea ta?", "fr": "Sur quoi les gens étaient-ils plus stricts à ton époque ?", "it": "Qualcosa di cui le persone erano più severe ai tuoi tempi?", "fi": "Mikä on jotain, mihin ihmiset olivat tiukempia sinun aikoinasi?", "nl": "Wat is iets waar mensen strenger over waren in jouw tijd?", "no": "Hva er noe folk var strengere på i din tid?", "sv": "Vad var något som folk var strängare med under din tid?", "pl": "Coś, co ludzie byli surowsi w twoich czasach?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something that makes you think, “Wow, times have really changed”?", "da": "Hvad er noget, der får dig til at tænke: “Wow, tiderne har virkelig ændret sig”?", "de": "Was bringt dich dazu zu denken: „Wow, die Zeiten haben sich wirklich verändert“?", "es": "¿Qué te hace pensar: “Vaya, los tiempos realmente han cambiado”?", "pt": "O que te faz pensar: “Uau, os tempos realmente mudaram”?", "ro": "Ce te face să spui: „Wow, vremurile chiar s-au schimbat”?", "fr": "Qu’est-ce qui te fait dire : « Wow, les temps ont vraiment changé » ?", "it": "Qualcosa che ti fa pensare: \"Wow, i tempi sono davvero cambiati\"?", "fi": "Mikä on jotain, mikä saa sinut ajattelemaan: \"Vau, ajat ovat todella muuttuneet\"?", "nl": "Wat is iets dat je doet denken: \"Wauw, de tijden zijn echt veranderd\"?", "no": "Hva er noe som får deg til å tenke: \"Wow, tider har virkelig endret seg\"?", "sv": "Vad är något som får dig att tänka, \"Wow, tiderna har verkligen förändrats\"?", "pl": "Coś, co sprawia, że myślisz: „Wow, czasy naprawdę się zmieniły”?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What was your favorite piece of “cool” tech when you were young?", "da": "Hvad var dit yndlingsstykke “sej” teknologi, da du var ung?", "de": "Was war dein Lieblingsstück „coole“ Technik, als du jung warst?", "es": "¿Cuál era tu tecnología “genial” favorita cuando eras joven?", "pt": "Qual era a tua tecnologia “fixe” favorita quando eras jovem?", "ro": "Care era tehnologia ta „cool” preferată când erai tânăr?", "fr": "Quel était ton gadget ou ta technologie « cool » préférée quand tu étais jeune ?", "it": "Qual era il tuo pezzo di tecnologia \"cool\" preferito quando eri giovane?", "fi": "Mikä oli suosikkisi \"siististä\" teknologiasta, kun olit nuori?", "nl": "Wat was jouw favoriete stuk \"coole\" technologie toen je jong was?", "no": "Hva var ditt favorittstykke av \"kult\" teknologi da du var ung?", "sv": "Vad var din favoritpryl av \"cool\" teknik när du var ung?", "pl": "Jaki był twój ulubiony kawałek \"fajnej\" technologii, gdy byłeś młody?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s one thing you hope never changes?", "da": "Hvad er én ting, du håber aldrig ændrer sig?", "de": "Was ist eine Sache, von der du hoffst, dass sie sich nie ändert?", "es": "¿Qué cosa esperas que nunca cambie?", "pt": "O que esperas que nunca mude?", "ro": "Ce lucru speri să nu se schimbe niciodată?", "fr": "Quelle chose espères-tu qu’elle ne change jamais ?", "it": "Qual è una cosa che speri non cambi mai?", "fi": "Mikä on yksi asia, jonka toivot pysyvän muuttumattomana?", "nl": "Wat is één ding waarvan je hoopt dat het nooit verandert?", "no": "Hva er én ting du håper aldri endrer seg?", "sv": "Vad är en sak du hoppas aldrig förändras?", "pl": "Co jest jedną rzeczą, która, mam nadzieję, nigdy się nie zmieni?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "Tell me about a moment that changed the direction of your life.", "da": "Fortæl mig om et øjeblik, der ændrede retningen i dit liv.", "de": "Erzähl mir von einem Moment, der die Richtung deines Lebens verändert hat.", "es": "Cuéntame sobre un momento que cambió el rumbo de tu vida.", "pt": "Conta-me sobre um momento que mudou a direção da tua vida.", "ro": "Spune-mi despre un moment care ți-a schimbat direcția vieții.", "fr": "Raconte-moi un moment qui a changé la direction de ta vie.", "it": "Raccontami di un momento che ha cambiato la direzione della tua vita.", "fi": "Kerro minulle hetkestä, joka muutti elämäsi suuntaa.", "nl": "Vertel me over een moment dat de richting van je leven heeft veranderd.", "no": "Fortell meg om et øyeblikk som endret retningen i livet ditt.", "sv": "Berätta om ett ögonblick som förändrade riktningen i ditt liv.", "pl": "Opowiedz mi o momencie, który zmienił kierunek twojego życia."}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s a small decision that ended up changing a lot for you?", "da": "Hvad er en lille beslutning, der endte med at ændre meget for dig?", "de": "Was ist eine kleine Entscheidung, die viel für dich verändert hat?", "es": "¿Qué pequeña decisión terminó cambiando mucho para ti?", "pt": "Que pequena decisão acabou por mudar muito para ti?", "ro": "Ce mică decizie a ajuns să-ți schimbe mult viața?", "fr": "Quelle petite décision a fini par changer beaucoup pour toi ?", "it": "Qual è una piccola decisione che alla fine ha cambiato molto per te?", "fi": "Mikä on pieni päätös, joka lopulta muutti paljon elämässäsi?", "nl": "Wat is een kleine beslissing die uiteindelijk veel voor jou heeft veranderd?", "no": "Hva er en liten beslutning som endte opp med å forandre mye for deg?", "sv": "Vad är ett litet beslut som slutade med att förändra mycket för dig?", "pl": "Jakieś małe postanowienie, które ostatecznie zmieniło dla ciebie wiele?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something people used to fix themselves that everyone replaces now?", "da": "Hvad plejede folk at reparere selv, som alle udskifter nu?", "de": "Was haben die Leute früher selbst repariert, was heute jeder ersetzt?", "es": "¿Qué solían arreglar las personas antes que ahora todos reemplazan?", "pt": "O que as pessoas consertavam antes e agora todos substituem?", "ro": "Ce obișnuiau oamenii să repare singuri, dar acum înlocuiesc?", "fr": "Que réparaient autrefois les gens eux-mêmes que tout le monde remplace maintenant ?", "it": "Qualcosa che le persone si riparavano da sole e che ora tutti sostituiscono?", "fi": "Mikä on jotain, mitä ihmiset ennen korjasivat itse, mutta mikä nykyään vaihdetaan?", "nl": "Wat is iets dat mensen vroeger zelf repareerden, maar wat nu iedereen vervangt?", "no": "Hva er noe folk pleide å fikse selv, som alle nå bytter ut?", "sv": "Vad är något som folk brukade laga själva men som alla nu byter ut?", "pl": "Coś, co ludzie kiedyś naprawiali sami, a teraz wszyscy wymieniają?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something you had to wait for that’s instant today?", "da": "Hvad ventede du på dengang, som er øjeblikkeligt i dag?", "de": "Worauf musstest du früher warten, das heute sofort da ist?", "es": "¿Qué tenías que esperar antes que ahora es instantáneo?", "pt": "O que tinhas de esperar antes e hoje é instantâneo?", "ro": "Ce trebuia să aștepți înainte și acum se întâmplă instantaneu?", "fr": "Que devais-tu attendre autrefois et qui est instantané aujourd’hui ?", "it": "Qualcosa per cui hai dovuto aspettare e che oggi è istantaneo?", "fi": "Mikä on jotain, jota jouduit odottamaan, mutta joka on tänään välitöntä?", "nl": "Wat is iets waar je op moest wachten dat vandaag de dag direct beschikbaar is?", "no": "Hva er noe du måtte vente på som er øyeblikkelig i dag?", "sv": "Vad är något du var tvungen att vänta på som är ögonblickligt idag?", "pl": "Coś, na co musiałeś czekać, a co dzisiaj jest natychmiastowe?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What was your favorite thing to watch on TV — and when did it come on?", "da": "Hvad var dit yndlingsprogram i tv — og hvornår kom det på?", "de": "Was war deine Lieblingssendung im Fernsehen – und wann lief sie?", "es": "¿Cuál era tu programa favorito en la televisión — y cuándo se emitía?", "pt": "Qual era o teu programa favorito na TV — e quando dava?", "ro": "Care era emisiunea ta preferată la TV — și când era difuzată?", "fr": "Quelle était ton émission préférée à la télé — et quand passait-elle ?", "it": "Qual era la tua cosa preferita da guardare in TV — e quando andava in onda?", "fi": "Mikä oli lempijuttusi, jota katsoit televisiosta — ja milloin se tuli?", "nl": "Wat was je favoriete programma om op tv te kijken — en wanneer kwam het op?", "no": "Hva var din favoritt å se på TV — og når gikk det?", "sv": "Vad var din favoritgrej att titta på på TV — och när sändes den?", "pl": "Co było twoją ulubioną rzeczą do oglądania w telewizji — i kiedy to leciało?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s one thing you think people had more patience for back then?", "da": "Hvad er én ting, du tror, folk havde mere tålmodighed med dengang?", "de": "Womit glaubst du, hatten die Menschen früher mehr Geduld?", "es": "¿Con qué crees que la gente tenía más paciencia antes?", "pt": "Com o que achas que as pessoas tinham mais paciência antigamente?", "ro": "Cu ce crezi că aveau oamenii mai multă răbdare înainte?", "fr": "Pour quoi penses-tu que les gens avaient plus de patience autrefois ?", "it": "Qual è una cosa per cui pensi che le persone avessero più pazienza ai tempi?", "fi": "Mikä on yksi asia, johon luulet ihmisten olleen kärsivällisempiä ennen?", "nl": "Wat is één ding waar mensen vroeger meer geduld voor hadden, denk je?", "no": "Hva er én ting du tror folk hadde mer tålmodighet for den gang?", "sv": "Vad är en sak som du tror att folk hade mer tålamod för då?", "pl": "Coś, co myślisz, że ludzie mieli więcej cierpliwości do tego kiedyś?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s a job that barely exists now but used to be common?", "da": "Hvilket job findes næsten ikke mere, men var almindeligt før?", "de": "Welcher Beruf existiert heute kaum noch, war aber früher üblich?", "es": "¿Qué trabajo casi no existe ahora pero era común antes?", "pt": "Que profissão quase não existe agora, mas era comum antes?", "ro": "Ce meserie abia mai există acum, dar era comună înainte?", "fr": "Quel métier existe à peine maintenant mais était courant autrefois ?", "it": "Qual è un lavoro che esiste appena ora ma che era comune in passato?", "fi": "Mikä on työ, jota lähes ei enää ole, mutta joka oli ennen yleinen?", "nl": "Wat is een baan die bijna niet meer bestaat, maar vroeger gebruikelijk was?", "no": "Hva er en jobb som knapt eksisterer nå, men som pleide å være vanlig?", "sv": "Vilket jobb som knappt finns nu men som brukade vara vanligt?", "pl": "Jakie jest zajęcie, które prawie nie istnieje teraz, ale kiedyś było powszechne?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something that felt like luxury when you were growing up?", "da": "Hvad føltes som luksus, da du voksede op?", "de": "Was fühlte sich wie Luxus an, als du aufgewachsen bist?", "es": "¿Qué se sentía como un lujo cuando eras niño?", "pt": "O que parecia um luxo quando eras criança?", "ro": "Ce părea un lux când erai copil?", "fr": "Qu’est-ce qui te semblait être un luxe quand tu étais enfant ?", "it": "Qualcosa che sembrava un lusso quando eri giovane?", "fi": "Mikä oli jotain, mikä tuntui ylellisyydeltä lapsuudessasi?", "nl": "Wat was iets dat als luxe voelde toen je opgroeide?", "no": "Hva var noe som føltes som luksus da du vokste opp?", "sv": "Vad var något som kändes som lyx när du växte upp?", "pl": "Coś, co wydawało się luksusem, gdy dorastałeś?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something you think people took more pride in doing by hand?", "da": "Hvad er noget, du tror, folk var mere stolte af at lave i hånden?", "de": "Worin glaubst du, waren die Menschen früher stolzer, wenn sie es von Hand gemacht haben?", "es": "¿De qué crees que la gente se sentía más orgullosa de hacer a mano antes?", "pt": "Do que achas que as pessoas tinham mais orgulho em fazer à mão?", "ro": "De ce crezi că oamenii erau mai mândri să facă manual?", "fr": "De quoi penses-tu que les gens étaient plus fiers de faire à la main ?", "it": "Qual è qualcosa di cui pensi che le persone avessero più orgoglio a fare a mano?", "fi": "Mikä on jotain, mistä luulet ihmisten olevan ylpeämpiä, kun he tekevät sen käsin?", "nl": "Wat is iets waarvan je denkt dat mensen er meer trots op waren om het met de hand te doen?", "no": "Hva er noe du tror folk hadde mer stolthet i å gjøre for hånd?", "sv": "Vad är något som du tror att folk tog mer stolthet i att göra för hand?", "pl": "Coś, z czego myślisz, że ludzie bardziej czerpali dumę, robiąc to ręcznie?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s a smell that reminds you of “the old days”?", "da": "Hvilken duft minder dig om “de gode gamle dage”?", "de": "Welcher Geruch erinnert dich an „die guten alten Zeiten“?", "es": "¿Qué olor te recuerda a “los viejos tiempos”?", "pt": "Que cheiro te lembra “os bons velhos tempos”?", "ro": "Ce miros îți amintește de „vremurile bune de altădată”?", "fr": "Quelle odeur te rappelle “le bon vieux temps” ?", "it": "Qual è un odore che ti ricorda \"i bei tempi\"?", "fi": "Mikä tuoksu muistuttaa sinua \"vanhoista päivistä\"?", "nl": "Wat is een geur die je doet denken aan \"de oude tijden\"?", "no": "Hva er en lukt som minner deg om \"de gode gamle dager\"?", "sv": "Vilken doft påminner dig om \"de gamla dagarna\"?", "pl": "Jaki zapach przypomina ci \"stare dobre czasy\"?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something you used to do outside that kids rarely do now?", "da": "Hvad plejede du at lave udenfor, som børn sjældent gør nu?", "de": "Was hast du früher draußen gemacht, was Kinder heute selten tun?", "es": "¿Qué solías hacer afuera que los niños rara vez hacen ahora?", "pt": "O que costumavas fazer ao ar livre que as crianças raramente fazem agora?", "ro": "Ce făceai afară pe care copiii rar îl mai fac acum?", "fr": "Que faisais-tu dehors que les enfants font rarement aujourd’hui ?", "it": "Qualcosa che facevi all''aperto e che i bambini raramente fanno adesso?", "fi": "Mikä on jotain, mitä ennen teit ulkona, mutta mitä lapset harvoin tekevät nyt?", "nl": "Wat is iets dat je vroeger buiten deed wat kinderen nu zelden doen?", "no": "Hva er noe du pleide å gjøre ute som barn sjelden gjør nå?", "sv": "Vad är något du brukade göra utomhus som barn sällan gör nu?", "pl": "Coś, co kiedyś robiłeś na zewnątrz, a dzieci rzadko robią teraz?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something people collected before photos lived on phones?", "da": "Hvad samlede folk på, før billeder levede på telefoner?", "de": "Was sammelten die Leute, bevor Fotos auf Handys gespeichert wurden?", "es": "¿Qué coleccionaba la gente antes de que las fotos vivieran en los teléfonos?", "pt": "O que as pessoas colecionavam antes de as fotos viverem nos telemóveis?", "ro": "Ce colecționau oamenii înainte ca fotografiile să fie pe telefoane?", "fr": "Que collectionnaient les gens avant que les photos vivent sur les téléphones ?", "it": "Qualcosa che le persone collezionavano prima che le foto vivessero sui telefoni?", "fi": "Mitä asioita ihmiset keräsivät ennen kuin valokuvat olivat puhelimissa?", "nl": "Wat is iets dat mensen verzamelden voordat foto''s op telefoons stonden?", "no": "Hva er noe folk samlet på før bilder ble lagret på telefoner?", "sv": "Vad är något som folk samlade på innan foton fanns på telefoner?", "pl": "Coś, co ludzie zbierali, zanim zdjęcia zaczęły być przechowywane w telefonach?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something you remember hearing on the radio all the time?", "da": "Hvad kan du huske, du hørte i radioen hele tiden?", "de": "Was erinnerst du dich, ständig im Radio gehört zu haben?", "es": "¿Qué recuerdas haber escuchado en la radio todo el tiempo?", "pt": "O que te lembras de ouvir na rádio o tempo todo?", "ro": "Ce îți amintești că auzeai mereu la radio?", "fr": "Que te souviens-tu d’avoir entendu tout le temps à la radio ?", "it": "Qualcosa che ricordi di aver sentito alla radio tutto il tempo?", "fi": "Mikä on jotain, mitä muistat kuulleesi radiosta koko ajan?", "nl": "Wat is iets dat je je herinnert dat je vaak op de radio hoorde?", "no": "Hva er noe du husker å ha hørt på radioen hele tiden?", "sv": "Vad är något du minns att du hörde på radion hela tiden?", "pl": "Coś, co pamiętasz, że słyszałeś w radiu cały czas?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something you had to memorize because you couldn’t just look it up?", "da": "Hvad skulle du lære udenad, fordi du ikke bare kunne slå det op?", "de": "Was musstest du auswendig lernen, weil du es nicht einfach nachschlagen konntest?", "es": "¿Qué tenías que memorizar porque no podías simplemente buscarlo?", "pt": "O que tinhas de memorizar porque não podias simplesmente procurar?", "ro": "Ce trebuia să memorezi pentru că nu puteai doar să cauți?", "fr": "Qu’est-ce que tu devais mémoriser parce que tu ne pouvais pas simplement le chercher ?", "it": "Qualcosa che hai dovuto memorizzare perché non potevi semplicemente cercarlo?", "fi": "Mikä on jotain, jonka jouduit muistamaan, koska et voinut vain etsiä sitä?", "nl": "Wat is iets dat je moest onthouden omdat je het niet gewoon kon opzoeken?", "no": "Hva er noe du måtte lære deg utenat fordi du ikke kunne bare slå det opp?", "sv": "Vad är något du var tvungen att memorera eftersom du inte bara kunde slå upp det?", "pl": "Coś, co musiałeś zapamiętać, ponieważ nie mogłeś po prostu tego znaleźć?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something you think technology has made less personal?", "da": "Hvad tror du, teknologien har gjort mindre personlig?", "de": "Was hat die Technologie deiner Meinung nach unpersönlicher gemacht?", "es": "¿Qué crees que la tecnología ha hecho menos personal?", "pt": "O que achas que a tecnologia tornou menos pessoal?", "ro": "Ce crezi că a făcut tehnologia mai puțin personal?", "fr": "Qu’est-ce que la technologie a rendu moins personnel selon toi ?", "it": "Qualcosa che pensi la tecnologia abbia reso meno personale?", "fi": "Mikä on jotain, jonka ajattelet teknologian tehneen vähemmän henkilökohtaiseksi?", "nl": "Wat is iets waarvan je denkt dat technologie het minder persoonlijk heeft gemaakt?", "no": "Hva er noe du mener at teknologi har gjort mindre personlig?", "sv": "Vad är något som du tycker att teknologin har gjort mindre personligt?", "pl": "Coś, co myślisz, że technologia uczyniła mniej osobistym?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s one thing from today you wish had existed long ago?", "da": "Hvad er én ting fra i dag, du ville ønske havde eksisteret for længe siden?", "de": "Was ist eine Sache von heute, die du dir schon lange gewünscht hättest?", "es": "¿Qué cosa de hoy desearías que hubiera existido hace mucho tiempo?", "pt": "O que de hoje gostarias que tivesse existido há muito tempo?", "ro": "Ce lucru din prezent ți-ai fi dorit să existe de mult?", "fr": "Quelle chose d’aujourd’hui aurais-tu aimé qu’elle existe il y a longtemps ?", "it": "Qual è una cosa di oggi che avresti voluto esistesse tanto tempo fa?", "fi": "Mikä on yksi asia tänään, jonka toivoisit olevan olemassa jo kauan sitten?", "nl": "Wat is één ding van vandaag dat je had gewild dat het lang geleden al bestond?", "no": "Hva er én ting fra i dag du skulle ønske hadde eksistert for lenge siden?", "sv": "Vad är en sak från idag som du önskar hade funnits för länge sedan?", "pl": "Coś, co dzisiaj istnieje, co chciałbyś, żeby istniało już dawno temu?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something that cost only a few coins when you were young?", "da": "Hvad kostede kun få mønter, da du var ung?", "de": "Was hat nur ein paar Münzen gekostet, als du jung warst?", "es": "¿Qué costaba solo unas pocas monedas cuando eras joven?", "pt": "O que custava apenas umas moedas quando eras jovem?", "ro": "Ce costa doar câteva monede când erai tânăr?", "fr": "Qu’est-ce qui ne coûtait que quelques pièces quand tu étais jeune ?", "it": "Qualcosa che costava solo qualche moneta quando eri giovane?", "fi": "Mikä on jotain, joka maksoi vain muutaman kolikon, kun olit nuori?", "nl": "Wat is iets dat maar een paar muntjes kostte toen je jong was?", "no": "Hva er noe som kostet bare noen få mynter da du var ung?", "sv": "Vad var något som kostade bara några mynt när du var ung?", "pl": "Coś, co kosztowało tylko kilka monet, kiedy byłeś młody?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something about “the good old days” that actually wasn’t so good?", "da": "Hvad ved “de gode gamle dage” var faktisk ikke så godt?", "de": "Was an den „guten alten Zeiten“ war eigentlich nicht so gut?", "es": "¿Qué de “los viejos tiempos” en realidad no era tan bueno?", "pt": "O que dos “bons velhos tempos” afinal não era assim tão bom?", "ro": "Ce era în „vremurile bune de altădată” care de fapt nu era așa grozav?", "fr": "Qu’est-ce qui, dans “le bon vieux temps”, n’était pas si bien que ça ?", "it": "Qualcosa dei \"bei tempi andati\" che in realtà non era poi così buono?", "fi": "Mikä on jotain \"hyvistä vanhoista päivistä\", joka ei oikeastaan ollut niin hyvää?", "nl": "Wat is iets aan \"de goede oude tijd\" dat eigenlijk niet zo goed was?", "no": "Hva er noe med \"de gode gamle dager\" som faktisk ikke var så bra?", "sv": "Vad är något om \"de goda gamla dagarna\" som faktiskt inte var så bra?", "pl": "Coś, co w \"dawnych dobrych czasach\" nie było aż tak dobre?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something people worried about back then that seems funny now?", "da": "Hvad bekymrede folk sig om dengang, som virker sjovt nu?", "de": "Worum haben sich die Leute damals Sorgen gemacht, was heute lustig erscheint?", "es": "¿De qué se preocupaba la gente antes que ahora parece gracioso?", "pt": "Com o que as pessoas se preocupavam antes e que agora parece engraçado?", "ro": "De ce își făceau griji oamenii atunci, dar acum pare amuzant?", "fr": "De quoi les gens s’inquiétaient-ils autrefois et qui paraît drôle aujourd’hui ?", "it": "Qual è qualcosa di cui le persone si preoccupavano all''epoca che ora sembra divertente?", "fi": "Mikä on jotain, mistä ihmiset olivat huolissaan silloin, mikä tuntuu nyt hassulta?", "nl": "Wat is iets waar mensen zich toen zorgen over maakten dat nu grappig lijkt?", "no": "Hva er noe folk bekymret seg for den gang som virker morsomt nå?", "sv": "Vad är något som folk oroade sig för då som verkar lustigt nu?", "pl": "Coś, co ludzie się wtedy martwili, a teraz wydaje się zabawne?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s a rule your parents had that nobody follows anymore?", "da": "Hvad var en regel, dine forældre havde, som ingen følger mere?", "de": "Welche Regel hatten deine Eltern, die heute niemand mehr befolgt?", "es": "¿Qué regla tenían tus padres que ya nadie sigue?", "pt": "Que regra tinham os teus pais que já ninguém segue?", "ro": "Ce regulă aveau părinții tăi pe care nimeni nu o mai respectă azi?", "fr": "Quelle règle tes parents avaient-ils que plus personne ne suit maintenant ?", "it": "Qual è una regola che i tuoi genitori avevano e che nessuno segue più?", "fi": "Mikä on sääntö, jonka vanhempasi asettivat, mutta jota kukaan ei enää noudata?", "nl": "Wat is een regel die je ouders hadden die niemand meer volgt?", "no": "Hva er en regel foreldrene dine hadde som ingen følger lenger?", "sv": "Vilken regel hade dina föräldrar som ingen följer längre?", "pl": "Jakie zasady mieli twoi rodzice, których nikt już nie przestrzega?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s a piece of advice people said all the time when you were growing up?", "da": "Hvad var et råd, folk altid sagde, da du voksede op?", "de": "Welchen Ratschlag haben die Leute ständig gegeben, als du aufgewachsen bist?", "es": "¿Qué consejo decía la gente todo el tiempo cuando crecías?", "pt": "Que conselho as pessoas diziam sempre quando cresceste?", "ro": "Ce sfat spuneau oamenii mereu când erai copil?", "fr": "Quel conseil les gens donnaient-ils tout le temps quand tu grandissais ?", "it": "Qual è un consiglio che le persone dicevano sempre quando eri giovane?", "fi": "Mikä on neuvo, jota ihmiset sanoivat koko ajan, kun kasvoit?", "nl": "Wat is een advies dat mensen vaak zeiden toen je opgroeide?", "no": "Hva er et råd folk sa hele tiden da du vokste opp?", "sv": "Vilket råd sa folk hela tiden när du växte upp?", "pl": "Jaką radę ludzie często powtarzali, gdy dorastałeś?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something people did for fun without spending money?", "da": "Hvad gjorde folk for sjov uden at bruge penge?", "de": "Was haben die Leute zum Spaß gemacht, ohne Geld auszugeben?", "es": "¿Qué hacía la gente para divertirse sin gastar dinero?", "pt": "O que as pessoas faziam para se divertir sem gastar dinheiro?", "ro": "Ce făceau oamenii pentru distracție fără să cheltuiască bani?", "fr": "Que faisaient les gens pour s’amuser sans dépenser d’argent ?", "it": "Qual è una cosa che le persone facevano per divertirsi senza spendere soldi?", "fi": "Mikä on jotain, mitä ihmiset tekivät hauskaa ilman, että he käyttivät rahaa?", "nl": "Wat is iets dat mensen deden voor plezier zonder geld uit te geven?", "no": "Hva er noe folk gjorde for moro skyld uten å bruke penger?", "sv": "Vad är något som folk gjorde för skojs skull utan att spendera pengar?", "pl": "Coś, co ludzie robili dla zabawy, nie wydając pieniędzy?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something you remember from road trips before GPS?", "da": "Hvad husker du fra bilture før GPS fandtes?", "de": "Was erinnerst du dich an Autofahrten, bevor es GPS gab?", "es": "¿Qué recuerdas de los viajes por carretera antes del GPS?", "pt": "O que te lembras das viagens de carro antes do GPS?", "ro": "Ce îți amintești din călătoriile cu mașina înainte de GPS?", "fr": "Que te rappelles-tu des voyages en voiture avant le GPS ?", "it": "Qual è una cosa che ricordi dei viaggi in auto prima del GPS?", "fi": "Mikä on jotain, mitä muistat automatkoilta ennen GPS:ää?", "nl": "Wat is iets wat je je herinnert van roadtrips voordat er GPS was?", "no": "Hva er noe du husker fra bilturer før GPS?", "sv": "Vad är något du minns från bilresor innan GPS fanns?", "pl": "Coś, co pamiętasz z podróży samochodowych sprzed czasów GPS?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something people used to repair instead of replacing?", "da": "Hvad plejede folk at reparere i stedet for at erstatte?", "de": "Was haben die Leute früher repariert, anstatt es zu ersetzen?", "es": "¿Qué solían reparar las personas antes en lugar de reemplazarlo?", "pt": "O que as pessoas consertavam antes em vez de substituir?", "ro": "Ce obișnuiau oamenii să repare înainte în loc să înlocuiască?", "fr": "Que réparaient les gens autrefois au lieu de remplacer ?", "it": "Qualcosa che le persone riparavano invece di sostituire?", "fi": "Mikä on jotain, mitä ihmiset ennen korjasivat sen sijaan, että olisivat vaihtaneet?", "nl": "Wat is iets dat mensen vroeger repareerden in plaats van het te vervangen?", "no": "Hva er noe folk pleide å reparere i stedet for å erstatte?", "sv": "Vad är något som folk brukade laga istället för att byta ut?", "pl": "Coś, co ludzie kiedyś naprawiali zamiast wymieniać?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something you used to write by hand that’s typed now?", "da": "Hvad plejede du at skrive i hånden, som nu bliver skrevet på computer?", "de": "Was hast du früher von Hand geschrieben, das jetzt getippt wird?", "es": "¿Qué solías escribir a mano que ahora se escribe a máquina?", "pt": "O que costumavas escrever à mão que agora é digitado?", "ro": "Ce scriai de mână înainte și acum tastezi?", "fr": "Que écrivais-tu autrefois à la main et qui est tapé maintenant ?", "it": "Qualcosa che scrivevi a mano e che ora digiti?", "fi": "Mikä on jotain, mitä kirjoitit aiemmin käsin, mutta joka on nyt kirjoitettu tietokoneella?", "nl": "Wat is iets dat je vroeger met de hand schreef en nu getypt is?", "no": "Hva er noe du pleide å skrive for hånd som nå er skrevet på datamaskin?", "sv": "Vad är något du brukade skriva för hand som nu är typat?", "pl": "Coś, co kiedyś pisałeś ręcznie, a teraz piszesz na komputerze?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s a fashion trend you actually miss?", "da": "Hvilken modetrend savner du faktisk?", "de": "Welchen Modetrend vermisst du wirklich?", "es": "¿Qué tendencia de moda realmente extrañas?", "pt": "Que tendência de moda realmente sentes falta?", "ro": "Ce tendință de modă chiar îți lipsește?", "fr": "Quelle tendance de mode te manque vraiment ?", "it": "Qual è una tendenza di moda che ti manca davvero?", "fi": "Mikä muotitrendi on sellainen, jota oikeasti kaipaat?", "nl": "Welke mode trend mis je eigenlijk?", "no": "Hvilken motetrend savner du faktisk?", "sv": "Vilken modetrend saknar du faktiskt?", "pl": "Jaką modową tendencję rzeczywiście tęsknisz?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something that made childhood simpler — or tougher — back then?", "da": "Hvad gjorde barndommen enklere — eller sværere — dengang?", "de": "Was machte die Kindheit damals einfacher – oder schwieriger?", "es": "¿Qué hacía que la infancia fuera más fácil — o más difícil — en ese entonces?", "pt": "O que tornava a infância mais simples — ou mais difícil — naquela altura?", "ro": "Ce făcea copilăria mai ușoară — sau mai grea — atunci?", "fr": "Qu’est-ce qui rendait l’enfance plus simple — ou plus difficile — à l’époque ?", "it": "Qualcosa che ha reso l''infanzia più semplice — o più difficile — all''epoca?", "fi": "Mikä oli jotain, mikä teki lapsuudesta yksinkertaisempaa — tai vaikeampaa — silloin?", "nl": "Wat is iets dat de kindertijd toen eenvoudiger — of moeilijker — maakte?", "no": "Hva er noe som gjorde barndommen enklere — eller tøffere — den gangen?", "sv": "Vad var något som gjorde barndomen enklare — eller svårare — då?", "pl": "Coś, co sprawiło, że dzieciństwo było prostsze — lub trudniejsze — w tamtych czasach?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s one thing you think the older generation did better than today’s?", "da": "Hvad tror du, den ældre generation gjorde bedre end i dag?", "de": "Was glaubst du, hat die ältere Generation besser gemacht als heute?", "es": "¿Qué crees que la generación mayor hacía mejor que la de hoy?", "pt": "O que achas que a geração mais velha fazia melhor do que a de hoje?", "ro": "Ce crezi că făcea generația mai veche mai bine decât cea de azi?", "fr": "Que penses-tu que l’ancienne génération faisait mieux que celle d’aujourd’hui ?", "it": "Qual è una cosa che pensi che la generazione precedente facesse meglio di quella di oggi?", "fi": "Mikä on yksi asia, jonka ajattelet vanhemman sukupolven tehneen paremmin kuin nykyajan?", "nl": "Wat is één ding waarvan je denkt dat de oudere generatie dat beter deed dan vandaag de dag?", "no": "Hva er én ting du mener den eldre generasjonen gjorde bedre enn dagens?", "sv": "Vad är en sak som du tycker att den äldre generationen gjorde bättre än dagens?", "pl": "Co uważasz, że starsze pokolenie robiło lepiej niż dzisiejsze?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s one thing today’s generation definitely does better than the older?", "da": "Hvad er én ting, som dagens generation helt sikkert gør bedre end den ældre?", "de": "Was ist eine Sache, die die heutige Generation definitiv besser macht als die ältere?", "es": "¿Qué es algo que la generación actual definitivamente hace mejor que la anterior?", "pt": "O que é algo que a geração atual faz melhor do que a antiga?", "ro": "Ce este ceva ce generația de azi face cu siguranță mai bine decât cea veche?", "fr": "Quelle chose la génération d’aujourd’hui fait-elle définitivement mieux que l’ancienne ?", "it": "Qual è una cosa che la generazione di oggi fa sicuramente meglio delle generazioni precedenti?", "fi": "Mikä on yksi asia, jonka tämän päivän sukupolvi ehdottomasti tekee paremmin kuin vanhempi sukupolvi?", "nl": "Wat is één ding dat de huidige generatie zeker beter doet dan de oudere?", "no": "Hva er én ting dagens generasjon definitivt gjør bedre enn den eldre?", "sv": "Vad är en sak som dagens generation definitivt gör bättre än den äldre?", "pl": "Coś, co dzisiejsze pokolenie zdecydowanie robi lepiej niż starsze?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something people did for entertainment before everyone had screens?", "da": "Hvad gjorde folk for underholdning, før alle havde skærme?", "de": "Was haben die Leute zur Unterhaltung gemacht, bevor alle Bildschirme hatten?", "es": "¿Qué hacía la gente para entretenerse antes de que todos tuvieran pantallas?", "pt": "O que as pessoas faziam para se entreter antes de todos terem ecrãs?", "ro": "Ce făceau oamenii pentru divertisment înainte ca toți să aibă ecrane?", "fr": "Que faisaient les gens pour se divertir avant que tout le monde ait des écrans ?", "it": "Qual è una cosa che le persone facevano per divertirsi prima che tutti avessero degli schermi?", "fi": "Mitä ihmiset tekivät viihdykkeeksi ennen kuin kaikilla oli näyttöjä?", "nl": "Wat deden mensen voor vermaak voordat iedereen schermen had?", "no": "Hva var noe folk gjorde for underholdning før alle hadde skjermer?", "sv": "Vad gjorde folk för underhållning innan alla hade skärmar?", "pl": "Co ludzie robili dla rozrywki, zanim wszyscy mieli ekrany?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s one thing you think was safer back then — and one thing that wasn’t?", "da": "Hvad tror du, var sikrere dengang — og hvad var det ikke?", "de": "Was glaubst du, war damals sicherer – und was nicht?", "es": "¿Qué crees que era más seguro antes — y qué no lo era?", "pt": "O que achas que era mais seguro antes — e o que não era?", "ro": "Ce crezi că era mai sigur înainte — și ce nu era?", "fr": "Qu’est-ce qui, selon toi, était plus sûr autrefois — et quoi ne l’était pas ?", "it": "Qual è una cosa che pensi fosse più sicura allora — e una cosa che non lo era?", "fi": "Mikä on yksi asia, jonka ajattelet olleen turvallisempaa ennen — ja yksi asia, joka ei ollut?", "nl": "Wat is één ding dat je denkt dat veiliger was toen — en één ding dat dat niet was?", "no": "Hva er én ting du mener var tryggere før — og én ting som ikke var det?", "sv": "Vad är en sak som du tycker var säkrare då — och en sak som inte var det?", "pl": "Co według ciebie było jedną rzeczą, która była bezpieczniejsza w tamtych czasach — a co nie?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
VALUES ('The Good Old Days Talks', '{"en": "What’s something people used to plan weeks ahead that we do last-minute now?", "da": "Hvad plejede folk at planlægge uger i forvejen, som vi nu gør i sidste øjeblik?", "de": "Was haben die Leute früher wochenlang im Voraus geplant, das wir jetzt in letzter Minute machen?", "es": "¿Qué planificaban las personas con semanas de anticipación que ahora hacemos a último momento?", "pt": "O que as pessoas costumavam planear semanas antes e agora fazemos em cima da hora?", "ro": "Ce planificau oamenii cu săptămâni înainte, dar acum facem pe ultima sută de metri?", "fr": "Que planifiaient autrefois les gens des semaines à l’avance et que nous faisons à la dernière minute maintenant ?", "it": "Qualcosa che le persone pianificavano settimane in anticipo e che ora facciamo all''ultimo minuto?", "fi": "Mikä on jotain, mitä ihmiset ennen suunnittelivat viikkoja etukäteen, mutta teemme nyt viime hetkellä?", "nl": "Wat is iets waar mensen vroeger weken van tevoren plannen voor maakten, maar wat we nu op het laatste moment doen?", "no": "Hva er noe folk pleide å planlegge uker i forveien, som vi nå gjør i siste liten?", "sv": "Vad är något som folk brukade planera veckor i förväg som vi gör i sista minuten nu?", "pl": "Coś, co ludzie kiedyś planowali z wyprzedzeniem, a teraz robimy na ostatnią chwilę?"}'::jsonb, false, true, true);
INSERT INTO questions (category_name, translations, is_couple, is_friends, is_family)
