UPDATE questions
SET translations = '{"en": "Would you rather give up social media or give up watching movies?", "da": "Ville du hellere droppe sociale medier eller droppe film?", "de": "W\u00fcrdest du lieber auf soziale Medien oder auf Filme verzichten?", "es": "\u00bfPreferir\u00edas dejar las redes sociales o dejar de ver pel\u00edculas?", "pt": "Preferias desistir das redes sociais ou deixar de ver filmes?", "ro": "Ai prefera s\u0103 renun\u021bi la re\u021belele sociale sau la filme?", "fr": "Pr\u00e9f\u00e9rerais-tu abandonner les r\u00e9seaux sociaux ou arr\u00eater de regarder des films ?", "it": "Preferiresti rinunciare ai social media o rinunciare a guardare film?", "fi": "Luopuisitko mieluummin sosiaalisesta mediasta vai elokuvien katsomisesta?", "nl": "Zou je liever sociale media opgeven of liever geen films meer kijken?", "no": "Ville du heller gi opp sosiale medier eller gi opp \u00e5 se filmer?", "sv": "Skulle du hellre ge upp sociala medier eller ge upp att titta p\u00e5 filmer?", "pl": "Czy wola\u0142by\u015b zrezygnowa\u0107 z medi\u00f3w spo\u0142eczno\u015bciowych, czy zrezygnowa\u0107 z ogl\u0105dania film\u00f3w?"}'::jsonb,
    updated_at = NOW()
WHERE category_name = 'Would You Rather Talks'
  AND translations->>'en' = 'Would you rather give up social media or give up watching movies?';
